mapper.select:
select *
from (
select ROW_NUMBER() OVER(ORDER BY show_id DESC) AS show_no,
show_id, show_name, show_poster
FROM `show` s2
) AS show2
WHERE show2.show_no BETWEEN #{start} AND #{end}

select *
from (
    select ROW_NUMBER() OVER(ORDER BY show_id DESC) AS show_no,
    show_id, show_name, show_poster
    FROM `show` s2
    WHERE show_name LIKE CONCAT('%', #{query}, '%')
) AS show2
WHERE show2.show_no BETWEEN #{start} AND #{end}
